<?php/** * Created by PhpStorm. * User: jlee * Date: 10/17/2018 * Time: 10:34 AM */$servername = "localhost";$username = "johntheadmin";$password = "Echo120499!";$dbname = "nationgame";$conn = new mysqli($servername, $username, $password, $dbname);if (isset($_COOKIE["NationCookie"])) {	$nationCookie = $_COOKIE["NationCookie"];} else {	echo "<script>location.href='login.html'</script>";}$sql = "	SELECT nationId FROM Session WHERE sessionCookie = '$nationCookie'";if(NULL === $nation = $conn->query($sql)->fetch_assoc()) {	echo "<script>location.href='login.html'</script>";}$nationId = $nation['nationId'];$sql = "	SELECT 	population,	citizenIncome,	taxRate,	lastTaxCollection,	money	FROM Nation 	WHERE 	nationId = '$nationId'";$taxInfo = $conn->query($sql)->fetch_assoc();$currentTime = new DateTime();$lastTaxCollection = new DateTime($taxInfo['lastTaxCollection']);$daysSinceLastTaxCollection = $currentTime->diff($lastTaxCollection)->days;$citizenIncome = $taxInfo['citizenIncome'];$population = $taxInfo['population'];$taxRate = $taxInfo['taxRate'];$money = intval($taxInfo['money']);$citizenTaxPay = $taxRate/100*$citizenIncome;$totalCollection = intval($daysSinceLastTaxCollection*$population*$citizenTaxPay);$moneyAfterCollection = $money+$totalCollection;$currentTime = $currentTime->format('Y-m-d');$sql = "	UPDATE Nation	SET	money = '$moneyAfterCollection',	lastTaxCollection = '$currentTime'	WHERE	nationId = '$nationId'";$conn->query($sql);